import React, { Component } from 'react'
import { useState } from 'react';
import "./styles.css"
import { FaStar } from "react-icons/fa";

function Home()
        {
            return(

                <div>
                    <aside id="sidebar" className="nano">
                        <div className="nano-content">
                        <div className="logo-container">
                            <span className="logo glyphicon glyphicon-envelope"></span>
                            Mail
                        </div>
                        <a className="compose-button">Compose</a>
                        <menu className="menu-segment">                   
                            <ul>
                            <li className="active"><a href="#">Inbox<span> (43)</span></a></li>
                            <li><a href="#">Important</a></li>
                            <li><a href="#">Sent</a></li>
                            <li><a href="#">Drafts</a></li>
                            <li><a href="#">Trash</a></li>
                            </ul>
                        </menu>
                        </div>
                    </aside>

                    <main id="main"/>
                    <div className="overlay"></div>
                    <header className="header">
                      <div className="search-box">
                        <input placeholder="Search..."/><span className="icon glyphicon glyphicon-search"></span>
                      </div>
                      <h1 className="page-title"><a className="sidebar-toggle-btn trigger-toggle-sidebar"><span className="line"></span><span class="line"></span><span class="line"></span><span class="line line-angle1"></span><span className="line line-angle2"></span></a>Inbox<a><span class="icon glyphicon glyphicon-chevron-down"></span></a></h1>
                    </header>

                    {
                        //</div><div id="main-nano-wrapper" class="nano">
                        //<div class="nano-content">
                        //</div><ul class="message-list"/>
                    }
                </div>
            )
        }

export default Home;
